export class Formula {
  entropia(str) {
    const len = str.length;
    const frecuencia_str = Array.from(str).reduce((freq, c) => (freq[c] = (freq[c] || 0) + 1) && freq, {});
    const frecuencia = Object.values(frecuencia_str).reduce((sum, f) => sum - f / len * Math.log2(f / len), 0);

    const format = (num, decimals) => num.toLocaleString('en-US', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    });

    return Number(format(frecuencia, 2));
  }

}
